import React, { useEffect, useState } from "react";
import "./todostyle.css";
import { FaPenNib } from "react-icons/fa";
import { RiDeleteBin2Fill } from "react-icons/ri";

// to get from local storage
const getLocalItems = () => {
  let list = localStorage.getItem("lists");
  console.log(list);
  if (list) {
    return JSON.parse(localStorage.getItem("lists"));
  } else {
    return [];
  }
};

// Main function
function TodoList() {
  const [todos, setTodos] = useState(getLocalItems()); //contains the main data array []
  const [text, setText] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    const newTodo = {
      id: todos.length + 1,
      text: text,
      completed: false,
    };
    if (newTodo.text === "") {
      alert("Please 🙏 add ✍️ some task in input field 😊");
    } else {
      setTodos([...todos, newTodo]);
    }
    setText("");
  };
  //  if the id of checked element get matched then made changes in todos array and set completed to true
  const handleComplete = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    );
    setTodos(updatedTodos);
  };

  // Deleting items
  const deleteItem = (id) => {
    console.log(id);
    const updateditems = todos.filter((elm) => {
      return elm.id !== id;
    });
    setTodos(updateditems);
  };

  // Date time
  const formatDate = () => {
    const date = new Date();
    const options = {
      weekday: "long",
      month: "long",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
    };
    return date.toLocaleString("en-US", options);
  };

  // Clear all data/Items
  const removeall = () => {
    setTodos([]);
  };
  //adding data to localStorage
  useEffect(() => {
    localStorage.setItem("lists", JSON.stringify(todos));
  }, [todos]);

  return (
    <div>
      <h1>
        Todo List <FaPenNib />
      </h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="✍️Add todo"
          value={text}
          className="forminput"
          onChange={(event) => setText(event.target.value)}
        />
        <button type="submit">Add</button>
        {/* <button type="submit"> + </button> */}
      </form>
      <ul>
        {todos.map((todo) => (
          <>
            <button id="date">{formatDate()}</button>
            <li key={todo.id}>
              <input
                type="checkbox"
                checked={todo.completed}
                className="userindputfild"
                onChange={() => handleComplete(todo.id)}
              />
              <span
                style={{
                  textDecoration: todo.completed ? "line-through" : "none",
                }}
                className="spanfild"
              >
                {todo.text}
              </span>
              <RiDeleteBin2Fill
                onClick={() => deleteItem(todo.id)}
                className="bin"
              />
            </li>
          </>
        ))}
      </ul>
      <button onClick={removeall}>Clear All</button>
    </div>
  );
}

export default TodoList;
